/*
main_color: #ffffff
primary_color: #3d22a0
secondary_color: #4c57da
accent_color: #b873e2
*/

html, * {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Ubuntu, "Helvetica Neue", Helvetica, sans-serif;
}

body {
  margin: 0;
  background-color: #ffffff;
  color: #ffffff;
  font-family: "Inconsolata", sans-serif;
}

/* Colors and Fonts */
.primary-color {
  color: #3d22a0;
}

.secondary-color {
  color: #4c57da;
}

.accent-color {
  color: #b873e2;
}

.heading-one {
  font-family: 'Merriweather', serif;
  font-size: 65px;
}

.heading-two {
  font-family: 'Alegreya', serif;
  font-size: 52px;
}

.heading-three {
  font-family: 'Ek Mukta', serif;
  font-size: 32px;
}

.heading-four {
  font-family: 'Ek Mukta', serif;
  font-size: 24px;
}

.heading-five {
  font-family: 'Ek Mukta', serif;
  font-size: 17.5px;
}

.padding-section-one {
  padding: 0.75em 0.5em;
}

.padding-two {
  padding: 0.4em 0.2em;
}

/* Titles and Anchors */
.title {
  font-size: 42px;
  margin-bottom: 60px;
  color: #3d22a0;
  text-shadow: rgba(0, 0, 0, 0.25) 0px 14px 28px, rgba(0, 0, 0, 0.22) 0px 10px 10px;
}

a {
  text-decoration: none;
  color: #ffffff;
  padding-right: 7px;
}

.active {
  color: #b873e2 !important;
}


#resume-link {
  text-decoration: none;
  color: #3d22a0;
  padding-right: 7px;
}

h1 {
  /* font-size: 2em; */
  margin: 0.25em 0;
  text-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px;
}

h2 {
  margin: 0.15em 0;
  text-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px;
}

img {
  height: 681.25px;
  width: 563.125px;
  margin: 5px 60px;
  border-radius: 5px;
  box-shadow: rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px;
}

#sudoku-image, #investment-image {
  height: 275px;
  width: 550px;
  box-shadow: none;
  margin: 5px 30px 5px 5px;
}

.blazier-logo {
  height: 50px;
  width: auto;
  box-shadow: none;
  margin: 5px 20px;
}

/* Sections */
/* Header Section */
.header {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  color: #ffffff;
  background-color: #3d22a0;
  box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
  height: 4em;
  text-shadow: rgba(0, 0, 0, 0.25) 0px 14px 28px, rgba(0, 0, 0, 0.22) 0px 10px 10px;
}

.sticky {
  position: fixed;
  top: 0;
  width: 100%;
}

.icon, .nav {
  padding: 20px;
  text-decoration: none;
}

/* Home Section */
.home {
  height: 50em;
  width: 100%;
  margin-top: 4em;
  background-image: url('../images/home-background.png');
  background-position: top;
  background-repeat: no-repeat;
  background-size: cover;
}

.home-text {
  width: 45%;
  padding: 10em 0em 0em 5em;
  text-shadow: rgba(0, 0, 0, 0.25) 0px 14px 28px, rgba(0, 0, 0, 0.22) 0px 10px 10px;
}

/* About Me Section */
.about-me {
  display: flex;
  flex-direction: row;
  height: 45em;
  padding: 100px 50px 0px 50px;
  background-image: url('../images/aboutme-background.png');
  background-position: top;
  background-repeat: no-repeat;
  background-size: cover;
}

/* Skills Section */
.skills {
  height: 45em;
  padding: 100px 50px 0px 50px;
}

.icons-grp {
  display: flex;
  flex-direction: column;
  align-items: center;
}

.icons {
  font-size: 100px;
  padding: 50px 50px;
}

/* Experience Section */
.experience {
  height: 45em;
  padding: 100px 50px 0px 50px;
  background-image: url('../images/experience-background.png');
  background-position: top;
  background-repeat: no-repeat;
  background-size: cover;
}

.companies {
  display: flex;
  flex-direction: row;
  justify-content: center;
}

.company {
  padding: 10px;
  border: 1px solid #3d22a0;
  background-color: #3d22a0;
  width: 275px;
  color: #ffffff;
  margin: 25px 5px 0px 5px;
  box-shadow: rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px;
}

.company:hover {
  color: #b873e2;
}

.description {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 35px 100px;
  font-size: 18px;
}

/* Projects Section */
.projects {
  height: auto;
  padding: 100px 50px 0px 50px;
}

.boxes {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

.image-box {
  display: flex;
  align-items: center;
}

.box {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  border: 1px solid #4c57da;
  height: 275px;
  width: 300px;
  background-color: #4c57da;
  margin: 50px;
  border-radius: 5px;
  font-size: 16px;
  box-shadow: rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px;
}

/*   */

.box-icon {
  font-size: 20px;
  padding: 3px;
}

.box-icons {
  display: flex;
  justify-content: flex-end;
  padding: 10px 10px 0px 0px;
}

.box-icon:hover {
  color: #3d22a0;
}

.language {
  margin: 10px 20px;
}

.box-text {
  margin: -40px 20px 0px 20px;
}

/* Contacts Section */
.contact-me {
  height: 35em;
  padding: 100px 50px 0px 50px;
  font-size: 18px;
  text-align: center;
  background-image: url('../images/contact-background.png');
  background-position: top;
  background-repeat: no-repeat;
  background-size: cover;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}
.contact-message {
  display: flex;
  flex-direction: column;
}

.contact-text {
  padding: 50px 0px;
}

.social-medias {
  display: flex;
  flex-direction: row;
  justify-content: center;
  margin: 10px;
}

.social-media:hover {
  color: #b873e2;
}

.social-media {
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 10px;
}

.sm-icon {
  font-size: 40px;
}

.footer {
  display: flex;
  justify-content: center;
  margin: 0px 0px 25px 0px;
}

/* Buttons */
.btn {
  background-color: #3d22a0;
  color: #ffffff;
  border: none;
  border-radius: 5px;
  height: 3em;
  width: 12em;
  box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
  text-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px;
  margin: 20px 0px;
  font-family: 'Merriweather', serif;
}

a:hover, i:hover {
  color: #b873e2;
}

.btn:hover {
  background-color: #b873e2;
}


.name {
  display: flex;
}
